from Crypto.Cipher import AES
from Crypto.Util.Padding import pad
from base64 import b64encode

# AES Key  IV (16 Bytes   )
KEY = b'^#^#&@*HDU@&@*()'   
IV = b'^@%#&*NSHUE&$*#)'   

# URL  Encrypt   Function
def enc_url(url):
    cipher = AES.new(KEY, AES.MODE_CBC, IV)
    ciphertext = cipher.encrypt(pad(url.encode(), AES.block_size))
    return "helper://" + b64encode(ciphertext).decode('utf-8')

def encrypt_links_proper(obj):
    if isinstance(obj, dict):
        return {key: encrypt_links_proper(value) for key, value in obj.items()}
    elif isinstance(obj, list):
        return [encrypt_links_proper(item) for item in obj]
    elif isinstance(obj, str):
        match = re.search(r"(https?://\S+)", obj)  # Find URL in the string
        if match:
            encrypted_url = enc_url(match.group(1))  # Encrypt only the URL part
            return obj.replace(match.group(1), encrypted_url)  # Replace original URL with encrypted one
    return obj

